export type Without<T, U> = {
    [P in Exclude<keyof T, keyof U>]?: never;
};
export type XOR<T, U> = T | U extends object ? (Without<T, U> & U) | (Without<U, T> & T) : T | U;
export interface FixedInterval {
    fixed: number;
}
export interface RandomInterval {
    random: {
        min: number;
        max: number;
    };
}
export interface IntervalJob {
    interval: XOR<FixedInterval, RandomInterval>;
    run: () => Promise<unknown> | unknown;
}
export declare function createJob(interval: IntervalJob["interval"], run: IntervalJob["run"]): IntervalJob;
export declare function startAllIntervalJobs<T extends Record<string, IntervalJob>>(jobs: T): void;
